mapper.select:
select a.id,a.title,a.picture,a.channel_id AS channelId,a.category_id categoryId,a.user_id AS userId,a. hits,a.hot,a.status,a.deleted,a.created,a.updated, u.username, GROUP_CONCAT(t.tagname) as tags from cms_article as a left join cms_user u on u.id=a.user_id left join cms_article_tag_middle as atm on atm.aid=a.id LEFT join cms_tag as t on t.id=atm.tid
and a.channel_id=#{channelId}
and a.category_id=#{catId}
